
public class 단항연산자_증감 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		a--;
		a--;
		a--;
		System.out.println(a);
		
		int b = 10;
		--b;
		--b;
		--b;
		System.out.println(b);
		
		int c = 10;
		int d = c--;
		System.out.println(d);
		
		int e=10;
		int g = --e;
		System.out.println(g);
		// 찾기 단축버튼 ctrl + f = 단어를 찾고, 다른것으로 대체가능
		
	}

}
